/* ============================================
   LOADER STYLES
   ============================================ */
.loader {
  position: fixed;
  inset: 0;
  z-index: 100000;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--bg-primary);
}

.loader-content {
  position: relative;
  z-index: 2;
  text-align: center;
}

.loader-text {
  display: flex;
  gap: 4px;
  perspective: 1000px;
}

.loader-letter {
  font-family: 'Syne', sans-serif;
  font-size: clamp(4rem, 15vw, 10rem);
  font-weight: 800;
  background: var(--gradient-main);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  display: inline-block;
}

.loader-line {
  width: 200px;
  height: 2px;
  background: var(--gradient-main);
  margin: 30px auto 20px;
  transform-origin: left;
}

.loader-tagline {
  font-size: 14px;
  letter-spacing: 4px;
  text-transform: uppercase;
  color: var(--text-secondary);
}

.loader-bg {
  position: absolute;
  inset: 0;
  z-index: 1;
}

.loader-bg-block {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: var(--bg-primary);
  transform-origin: bottom;
}

/* ============================================
   NAVIGATION STYLES
   ============================================ */
.navigation {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  padding: 24px 48px;
  transition: all 0.5s var(--ease-out-expo);
}

.navigation.scrolled {
  padding: 16px 48px;
  background: rgba(5, 5, 5, 0.8);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid var(--border-color);
}

.nav-container {
  max-width: 1400px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.nav-logo {
  display: flex;
  align-items: center;
  gap: 6px;
  text-decoration: none;
}

.logo-text {
  font-family: 'Syne', sans-serif;
  font-size: 28px;
  font-weight: 800;
  background: var(--gradient-main);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.logo-dot {
  width: 8px;
  height: 8px;
  background: var(--accent-tertiary);
  border-radius: 50%;
}

.nav-links {
  display: flex;
  gap: 40px;
}

.nav-link {
  position: relative;
  font-size: 14px;
  font-weight: 500;
  color: var(--text-secondary);
  text-decoration: none;
  padding: 8px 0;
  transition: color 0.3s ease;
}

.nav-link:hover,
.nav-link.active {
