  font-size: 12px;
  font-weight: 600;
  color: var(--accent-primary);
  margin-bottom: 32px;
  position: relative;
  overflow: hidden;
}

.badge-glow {
  position: absolute;
  inset: 0;
  background: var(--gradient-main);
  opacity: 0.1;
  animation: badge-glow 3s ease-in-out infinite;
}

@keyframes badge-glow {
  0%, 100% { opacity: 0.05; }
  50% { opacity: 0.15; }
}

.contact-title {
  font-family: 'Syne', sans-serif;
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: 20px;
}

.contact-description {
  color: var(--text-secondary);
  font-size: 1.1rem;
  line-height: 1.7;
}

.contact-methods {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-bottom: 40px;
  max-width: 600px;
}

.contact-card {
  position: relative;
  display: flex;
  align-items: center;
  gap: 20px;
  padding: 28px;
  background: rgba(255, 255, 255, 0.02);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-lg);
  text-decoration: none;
  color: inherit;
  overflow: hidden;
  transition: all 0.4s var(--ease-out-expo);
}

.contact-card:hover {
  border-color: rgba(255, 255, 255, 0.15);
}

.contact-card-bg {
  position: absolute;
  inset: 0;
  opacity: 0;
  transition: opacity 0.4s ease;
}

.contact-card.whatsapp .contact-card-bg {
  background: linear-gradient(135deg, rgba(37, 211, 102, 0.1) 0%, transparent 100%);
}

.contact-card.email .contact-card-bg {
  background: var(--gradient-subtle);
}

.contact-card:hover .contact-card-bg {
  opacity: 1;
}

.contact-card-icon {
  width: 56px;
  height: 56px;
  border-radius: var(--radius-md);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
}

.contact-card.whatsapp .contact-card-icon {
  background: linear-gradient(135deg, #25d366 0%, #128c7e 100%);
  color: white;
}

.contact-card.email .contact-card-icon {
  background: var(--gradient-main);
  color: white;
}

.contact-card-content {
  flex: 1;
  position: relative;
  z-index: 1;
}

.contact-card-label {
  display: block;
  font-weight: 600;
  font-size: 1.1rem;
  margin-bottom: 4px;
}

.contact-card-value {
  color: var(--text-secondary);
  font-size: 0.9rem;
}

.contact-card-arrow {
  color: var(--text-muted);
  position: relative;
  z-index: 1;
  transition: all 0.3s ease;
}

.contact-card:hover .contact-card-arrow {
  color: var(--text-primary);
  transform: translate(4px, -4px);
}

.contact-availability {
  display: flex;
  align-items: center;
  gap: 14px;
  color: var(--text-secondary);
  font-size: 0.95rem;
}

.availability-indicator {
  position: relative;
  width: 12px;
  height: 12px;
}

.indicator-dot {
  position: absolute;
  inset: 2px;
  background: #22c55e;
  border-radius: 50%;
}

.indicator-ring {
  position: absolute;
  inset: 0;
  border: 1px solid rgba(34, 197, 94, 0.5);
  border-radius: 50%;
  animation: ring-pulse 2s infinite;
}

@keyframes ring-pulse {
  0% { transform: scale(1); opacity: 1; }
  100% { transform: scale(2); opacity: 0; }
}

.contact-decoration {
  position: absolute;
  top: 50%;
  right: 80px;
  transform: translateY(-50%);
  width: 300px;
  height: 300px;
  pointer-events: none;
}

.deco-svg {
  width: 100%;
  height: 100%;
}

@media (max-width: 1024px) {
  .contact-wrapper {
    padding: 48px;
  }

  .contact-decoration {
    display: none;
  }
}

@media (max-width: 768px) {
  .contact-methods {
    grid-template-columns: 1fr;
  }
}

/* ============================================
   FOOTER STYLES
   ============================================ */
.footer {
  margin-top: 100px;
  padding-top: 40px;
  border-top: 1px solid var(--border-color);
}

.footer-content {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.footer-brand {
  display: flex;
  align-items: center;
  gap: 16px;
}

.footer-logo {
  font-family: 'Syne', sans-serif;
  font-size: 1.5rem;
  font-weight: 700;
  background: var(--gradient-main);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.footer-divider {
  width: 1px;
  height: 24px;
  background: var(--border-color);
}

.footer-tagline {
  color: var(--text-secondary);
  font-size: 0.9rem;
}

.footer-copyright {
  color: var(--text-muted);
  font-size: 0.85rem;
}

@media (max-width: 768px) {
  .footer-content {
    flex-direction: column;
    gap: 20px;
    text-align: center;
  }
}

