}

.hero-cta {
    display: flex;
    gap: 1rem;
    flex-wrap: wrap;
}

.btn-primary, .btn-secondary {
    padding: 1rem 2rem;
    border: none;
    border-radius: 12px;
    font-size: 1rem;
    font-weight: 600;
    cursor: none;
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    text-decoration: none;
    display: inline-block;
    position: relative;
    overflow: hidden;
    transform-style: preserve-3d;
}

.btn-content {
    position: relative;
    z-index: 2;
}

.btn-glow {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: inherit;
    border-radius: inherit;
    filter: blur(20px);
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: 1;
}

.btn-primary:hover .btn-glow,
.btn-secondary:hover .btn-glow {
    opacity: 0.7;
    animation: pulse-glow 1.5s ease-in-out infinite;
}

@keyframes pulse-glow {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
}

.btn-primary {
    background: var(--gradient-primary);
    color: var(--bg-primary);
    box-shadow: var(--shadow-neon);
}

.btn-primary:hover {
    transform: translateY(-3px) rotateX(5deg) scale(1.05);
    box-shadow: 0 0 30px rgba(0, 212, 255, 0.7);
}

.btn-secondary {
    background: transparent;
    color: var(--neon-cyan);
    border: 2px solid var(--neon-cyan);
}

.btn-secondary:hover {
    background: var(--neon-cyan);
    color: var(--bg-primary);
    box-shadow: 0 0 30px rgba(0, 255, 136, 0.5);
    transform: translateY(-3px) rotateX(5deg) scale(1.05);
}

.hero-image {
    display: flex;
    justify-content: center;
    align-items: center;
}

.profile-container {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.orbit-container {
    position: absolute;
    width: 400px;
    height: 400px;
    animation: rotate-container 20s linear infinite;
}

.orbit {
    position: absolute;
    border: 1px solid rgba(0, 255, 136, 0.3);
    border-radius: 50%;
    animation: rotate 10s linear infinite;
}

.orbit-1 {
    width: 150px;
    height: 150px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    animation-duration: 8s;
}

.orbit-2 {
    width: 200px;
    height: 200px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    animation-duration: 12s;
    animation-direction: reverse;
}

.orbit-3 {
    width: 250px;
    height: 250px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    animation-duration: 15s;
}

.orbit-item {
    position: absolute;
    width: 40px;
    height: 40px;
    background: var(--gradient-primary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--bg-primary);
    font-size: 1.2rem;
    top: -20px;
    left: 50%;
    transform: translateX(-50%);
    box-shadow: 0 0 20px rgba(0, 255, 136, 0.5);
}

@keyframes rotate-container {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

@keyframes rotate {
    from { transform: translate(-50%, -50%) rotate(0deg); }
    to { transform: translate(-50%, -50%) rotate(360deg); }
}

.profile-placeholder {
    width: 300px;
    height: 300px;
    border-radius: 50%;
    background: var(--gradient-secondary);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 4rem;
    color: var(--bg-primary);
    box-shadow: var(--shadow-glow);
    animation: float 3s ease-in-out infinite;
    position: relative;
    transform-style: preserve-3d;
    z-index: 10;
}

.profile-placeholder::before {
    content: '';
    position: absolute;
    top: -10px;
    left: -10px;
    right: -10px;
    bottom: -10px;
    background: var(--gradient-primary);
    border-radius: 50%;
    z-index: -1;
    opacity: 0.5;
    filter: blur(20px);
    animation: pulse 2s ease-in-out infinite;
}

@keyframes pulse {
    0%, 100% { transform: scale(1); opacity: 0.5; }
    50% { transform: scale(1.1); opacity: 0.8; }
}

@keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-20px); }
}

/* Responsive Tech Ecosystem */
@media (max-width: 768px) {
    .hero-content {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 2rem;
    }
    
    .hero-left {
        max-width: 100%;
    }
    
    .hero-headline {
        font-size: clamp(1.5rem, 6vw, 2.2rem);
    }
    
    .hero-subheadline {
        font-size: 1rem;
    }
    
    .hero-micro-proof {
        font-size: 1rem;
    }
    
    .hero-trust-line {
        font-size: 0.9rem;
    }
    
    .hero-right {
        height: 400px;
    }
    
    .tech-ecosystem {
        width: 100%;
        max-width: 350px;
        height: 350px;
    }
    
    .tech-core {
        width: 80px;
        height: 80px;
    }
    
    .core-face {
        width: 80px;
        height: 80px;
    }
    
    .core-front {
        transform: translateZ(40px);
    }
    
    .core-back {
        transform: rotateY(180deg) translateZ(40px);
    }
    
    .core-left {
        transform: rotateY(-90deg) translateZ(40px);
    }
    
    .core-right {
        transform: rotateY(90deg) translateZ(40px);
    }
    
    .core-top {
        transform: rotateX(90deg) translateZ(40px);
    }
    
    .core-bottom {
        transform: rotateX(-90deg) translateZ(40px);
    }
    
    .service-module {
        width: 140px;
        padding: 1rem;
    }
    
    .module-icon {
        width: 30px;
        height: 30px;
        font-size: 1rem;
    }
    
    .module-title {
        font-size: 0.9rem;
    }
    
    .module-value {
        font-size: 0.75rem;
    }
    
    /* Mobile carousel behavior for modules */
    .service-modules {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        gap: 1rem;
        overflow-x: auto;
        overflow-y: hidden;
        scroll-snap-type: x mandatory;
        -webkit-overflow-scrolling: touch;
    }
    
    .service-module {
        position: relative;
        top: auto;
        left: auto;
        right: auto;
        bottom: auto;
        transform: none !important;
        scroll-snap-align: center;
        min-width: 140px;
    }
    
    .tech-core {
        position: absolute;
        top: 20px;
        left: 50%;
        transform: translateX(-50%);
        z-index: 10;
    }
    
    .connection-lines {
