@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&family=Syne:wght@400;500;600;700;800&family=Instrument+Serif:ital@0;1&display=swap');

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  cursor: none;
}

:root {
  --bg-primary: #050505;
  --bg-secondary: #0a0a0a;
  --text-primary: #fafafa;
  --text-secondary: rgba(255, 255, 255, 0.5);
  --text-muted: rgba(255, 255, 255, 0.3);
  --accent-primary: #6366f1;
  --accent-secondary: #a855f7;
  --accent-tertiary: #ec4899;
  --gradient-main: linear-gradient(135deg, #6366f1 0%, #a855f7 50%, #ec4899 100%);
  --gradient-subtle: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(168, 85, 247, 0.1) 50%, rgba(236, 72, 153, 0.1) 100%);
  --border-color: rgba(255, 255, 255, 0.06);
  --glass-bg: rgba(255, 255, 255, 0.02);
  --glass-border: rgba(255, 255, 255, 0.05);
  --radius-sm: 8px;
  --radius-md: 16px;
  --radius-lg: 24px;
  --radius-xl: 32px;
  --radius-full: 9999px;
  --ease-out-expo: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-out-quart: cubic-bezier(0.25, 1, 0.5, 1);
}

html {
  scroll-behavior: smooth;
  scrollbar-width: thin;
  scrollbar-color: var(--accent-primary) var(--bg-primary);
}

::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: var(--bg-primary);
}

::-webkit-scrollbar-thumb {
  background: var(--gradient-main);
  border-radius: 10px;
}

body {
  font-family: 'Space Grotesk', -apple-system, sans-serif;
  background: var(--bg-primary);
  color: var(--text-primary);
  overflow-x: hidden;
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

.app {
  position: relative;
  min-height: 100vh;
}

.progress-bar {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: var(--gradient-main);
  transform-origin: 0%;
  z-index: 10000;
}

.grain-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 9999;
  opacity: 0.035;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 512 512' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E");
}

.ambient-light {
  position: fixed;
  inset: 0;
  overflow: hidden;
  pointer-events: none;
  z-index: 0;
}

.light {
  position: absolute;
  border-radius: 50%;
  filter: blur(150px);
  opacity: 0.4;
  mix-blend-mode: screen;
}

.light-1 {
  width: 800px;
  height: 800px;
  background: radial-gradient(circle, rgba(99, 102, 241, 0.4) 0%, transparent 70%);
  top: -300px;
  right: -200px;
  animation: floatLight1 25s ease-in-out infinite;
}

.light-2 {
  width: 600px;
  height: 600px;
  background: radial-gradient(circle, rgba(168, 85, 247, 0.35) 0%, transparent 70%);
  bottom: -200px;
  left: -150px;
  animation: floatLight2 30s ease-in-out infinite;
}

.light-3 {
  width: 500px;
  height: 500px;
  background: radial-gradient(circle, rgba(236, 72, 153, 0.3) 0%, transparent 70%);
  top: 40%;
  left: 30%;
  animation: floatLight3 20s ease-in-out infinite;
}

.light-4 {
  width: 400px;
  height: 400px;
  background: radial-gradient(circle, rgba(99, 102, 241, 0.25) 0%, transparent 70%);
  bottom: 20%;
  right: 10%;
  animation: floatLight4 22s ease-in-out infinite;
}

@keyframes floatLight1 {
  0%, 100% { transform: translate(0, 0) scale(1); }
  25% { transform: translate(-50px, 50px) scale(1.1); }
  50% { transform: translate(-100px, -30px) scale(0.95); }
  75% { transform: translate(30px, -50px) scale(1.05); }
}

@keyframes floatLight2 {
  0%, 100% { transform: translate(0, 0) scale(1); }
  33% { transform: translate(80px, -60px) scale(1.15); }
  66% { transform: translate(-40px, 40px) scale(0.9); }
}

@keyframes floatLight3 {
  0%, 100% { transform: translate(0, 0) rotate(0deg); }
  50% { transform: translate(100px, -80px) rotate(180deg); }
}

@keyframes floatLight4 {
  0%, 100% { transform: translate(0, 0); opacity: 0.3; }
  50% { transform: translate(-60px, 60px); opacity: 0.5; }
}

section {
  position: relative;
  z-index: 1;
  padding: 140px 48px;
}

.container {
  max-width: 1400px;
  margin: 0 auto;
  width: 100%;
}

.section-header {
  margin-bottom: 100px;
}

.section-eyebrow {
  display: inline-flex;
  align-items: center;
  gap: 12px;
  font-size: 11px;
  font-weight: 600;
  letter-spacing: 4px;
  text-transform: uppercase;
  color: var(--accent-primary);
  margin-bottom: 24px;
}

.section-eyebrow::before {
  content: '';
  width: 40px;
  height: 1px;
  background: var(--gradient-main);
}

.section-title {
  font-family: 'Syne', sans-serif;
  font-size: clamp(3rem, 6vw, 5rem);
  font-weight: 700;
  line-height: 1.05;
  letter-spacing: -0.03em;
}

.section-title .gradient-text {
  background: var(--gradient-main);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.section-title .serif {
  font-family: 'Instrument Serif', serif;
  font-style: italic;
  font-weight: 400;
}

.magnetic-btn {
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  padding: 20px 40px;
  font-family: 'Space Grotesk', sans-serif;
  font-size: 14px;
  font-weight: 600;
  letter-spacing: 0.5px;
  color: white;
  background: var(--gradient-main);
  border: none;
  border-radius: var(--radius-full);
  cursor: none;
  overflow: hidden;
  transition: transform 0.4s var(--ease-out-expo), box-shadow 0.4s var(--ease-out-expo);
  text-decoration: none;
}

.magnetic-btn::before {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(135deg, rgba(255,255,255,0.25) 0%, transparent 50%);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.magnetic-btn:hover {
  transform: scale(1.05);
  box-shadow: 0 20px 60px rgba(99, 102, 241, 0.4), 0 0 100px rgba(168, 85, 247, 0.2);
}

.magnetic-btn:hover::before {
  opacity: 1;
}

.magnetic-btn .btn-text {
  position: relative;
  z-index: 1;
}

.magnetic-btn .btn-icon {
  position: relative;
  z-index: 1;
  transition: transform 0.3s var(--ease-out-expo);
}

.magnetic-btn:hover .btn-icon {
  transform: translateX(4px);
}

.outline-btn {
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  padding: 20px 40px;
  font-family: 'Space Grotesk', sans-serif;
  font-size: 14px;
  font-weight: 600;
  letter-spacing: 0.5px;
  color: var(--text-primary);
  background: transparent;
  border: 1px solid var(--border-color);
  border-radius: var(--radius-full);
  cursor: none;
  overflow: hidden;
  transition: all 0.4s var(--ease-out-expo);
  text-decoration: none;
}

.outline-btn::before {
  content: '';
  position: absolute;
  inset: 0;
  background: var(--gradient-subtle);
  opacity: 0;
  transition: opacity 0.4s ease;
}

.outline-btn:hover {
  border-color: rgba(255, 255, 255, 0.15);
}

.outline-btn:hover::before {
  opacity: 1;
}

.glass-card {
  background: var(--glass-bg);
  backdrop-filter: blur(40px);
  -webkit-backdrop-filter: blur(40px);
  border: 1px solid var(--glass-border);
  border-radius: var(--radius-xl);
  transition: all 0.5s var(--ease-out-expo);
}

.glass-card:hover {
  background: rgba(255, 255, 255, 0.04);
  border-color: rgba(255, 255, 255, 0.1);
  transform: translateY(-8px);
  box-shadow: 0 40px 80px rgba(0, 0, 0, 0.3);
}

.text-reveal {
  overflow: hidden;
}

.text-reveal span {
  display: inline-block;
}

.split-text {
  display: inline-block;
}

@media (max-width: 1024px) {
  section {
    padding: 100px 32px;
  }

  .section-header {
    margin-bottom: 64px;
  }
}

@media (max-width: 768px) {
  section {
    padding: 80px 20px;
  }

  .section-header {
    margin-bottom: 48px;
  }

  .magnetic-btn, .outline-btn {
    padding: 16px 32px;
    font-size: 13px;
  }

  .light-1, .light-2, .light-3, .light-4 {
    opacity: 0.25;
  }
}

