const Portfolio = ({ onHover, onLeave }) => {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, margin: "-100px" });
  const [hoveredId, setHoveredId] = useState(null);

  const containerVariants = {
    hidden: {},
    visible: {
      transition: {
        staggerChildren: 0.15
      }
    }
  };

  const projectVariants = {
    hidden: { opacity: 0, y: 80 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.9,
        ease: [0.16, 1, 0.3, 1]
      }
    }
  };

  return (
    <section id="portfolio" className="portfolio-section" ref={containerRef}>
      <div className="container">
        <motion.div 
          className="section-header"
          initial={{ opacity: 0, y: 40 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, ease: [0.16, 1, 0.3, 1] }}
        >
          <span className="section-eyebrow">Selected Work</span>
          <h2 className="section-title">
            Projects that<br />
            <span className="serif">speak</span> <span className="gradient-text">volumes</span>
          </h2>
        </motion.div>

        <motion.div 
          className="portfolio-grid"
          variants={containerVariants}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {projects.map((project, index) => (
            <motion.article 
              key={project.id}
              className={`portfolio-item ${hoveredId === project.id ? 'hovered' : ''}`}
              variants={projectVariants}
              onMouseEnter={() => { setHoveredId(project.id); onHover(); }}
              onMouseLeave={() => { setHoveredId(null); onLeave(); }}
            >
              <div className="portfolio-image-container">
                <motion.div 
                  className="portfolio-image"
                  style={{ backgroundImage: `url(${project.image})` }}
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.6, ease: [0.16, 1, 0.3, 1] }}
                />
                <div className="portfolio-image-overlay">
                  <motion.div 
                    className="view-project-btn"
                    initial={{ opacity: 0, scale: 0.8 }}
                    whileHover={{ scale: 1.1 }}
                    animate={hoveredId === project.id ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
                    transition={{ duration: 0.3 }}
                  >
                    <ExternalLink size={24} />
                  </motion.div>
                </div>
                <div className="portfolio-year">{project.year}</div>
              </div>

              <div className="portfolio-content">
                <div className="portfolio-meta">
                  <span className="portfolio-category">{project.category}</span>
                  <ArrowUpRight className="portfolio-arrow" size={18} />
                </div>
                <h3 className="portfolio-title">{project.title}</h3>
                <p className="portfolio-description">{project.description}</p>
                <div className="portfolio-tags">
                  {project.tags.map((tag, i) => (
                    <span key={i} className="portfolio-tag">{tag}</span>
                  ))}
                </div>
              </div>
            </motion.article>
          ))}
        </motion.div>

        <motion.div 
          className="portfolio-cta"
          initial={{ opacity: 0, y: 40 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6, ease: [0.16, 1, 0.3, 1] }}
        >
          <div className="cta-content">
            <p>Interested in working together?</p>
            <a 
              href="#contact" 
              className="outline-btn"
              onMouseEnter={onHover}
              onMouseLeave={onLeave}
            >
              <span>Let's Discuss Your Project</span>
              <ArrowUpRight size={16} />
            </a>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Portfolio;

New-folderzip/client/src/App.jsx.content:
import { useEffect, useState, useRef } from 'react';
import { motion, AnimatePresence, useScroll, useTransform } from 'framer-motion';
import Hero from './components/Hero';
import Services from './components/Services';
import Portfolio from './components/Portfolio';
import WhyMe from './components/WhyMe';
import Contact from './components/Contact';
import CustomCursor from './components/CustomCursor';
import Navigation from './components/Navigation';
import Loader from './components/Loader';
import './App.css';

function App() {
  const [loading, setLoading] = useState(true);
  const [cursorVariant, setCursorVariant] = useState('default');
  const containerRef = useRef(null);

  const { scrollYProgress } = useScroll();
  const scaleX = useTransform(scrollYProgress, [0, 1], [0, 1]);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 1800);
    return () => clearTimeout(timer);
  }, []);

  const cursorEnter = () => setCursorVariant('hover');
  const cursorLeave = () => setCursorVariant('default');

  return (
    <>
      <AnimatePresence mode="wait">
        {loading && <Loader key="loader" />}
      </AnimatePresence>

      <AnimatePresence>
        {!loading && (
          <motion.div 
            className="app"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <CustomCursor variant={cursorVariant} />
            <Navigation onHover={cursorEnter} onLeave={cursorLeave} />

            <motion.div 
              className="progress-bar"
              style={{ scaleX }}
            />

            <div className="grain-overlay"></div>

            <div className="ambient-light">
              <div className="light light-1"></div>
              <div className="light light-2"></div>
              <div className="light light-3"></div>
              <div className="light light-4"></div>
            </div>

            <main ref={containerRef}>
              <Hero onHover={cursorEnter} onLeave={cursorLeave} />
              <Services onHover={cursorEnter} onLeave={cursorLeave} />
              <Portfolio onHover={cursorEnter} onLeave={cursorLeave} />
              <WhyMe onHover={cursorEnter} onLeave={cursorLeave} />
              <Contact onHover={cursorEnter} onLeave={cursorLeave} />
            </main>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

export default App;
